		            testRunner.testCase.setPropertyValue( "RESRAWINRESQUE" , resraw )
		            def chhannelname = eElement.getElementsByTagName("CHANNELNAME").item(0).getTextContent()
		            testRunner.testCase.setPropertyValue( "CHANNELNAME" , chhannelname )
