NodeList nList = doc.getElementsByTagName("TEST_CASE")
length = nList.getLength()
for (int temp = 0; temp < length; temp++) {
	Node nNode = nList.item(temp);
	if (nNode.getNodeType() == Node.ELEMENT_NODE) {
	       Element eElement = (Element) nNode;            
